"use strict";(self.webpackChunkdevdocs=self.webpackChunkdevdocs||[]).push([[19044],{87102:e=>{e.exports=JSON.parse('{"label":"form","permalink":"/moodledevdocs/docs/4.4/tags/form","allTagsPath":"/moodledevdocs/docs/4.4/tags","count":3,"items":[{"id":"apis/subsystems/form/fields/choicedropdown","title":"Choice Dropdown","description":"The choicedropdown form field creates a dropdown list with multiple options. It is different from a standard select dropdown in that each option can have extra icons and descriptions. This field is often used in forms to facilitate the selection of a non-trivial option that demands additional information.","permalink":"/moodledevdocs/docs/4.4/apis/subsystems/form/fields/choicedropdown"},{"id":"apis/subsystems/form/usage/index","title":"Form Usage","description":"Moodle\'s Form API is an extension of the Pear HTMLQuickForm API, which is no longer supported. Some documentation for the upstream library is available in the PEAR package page, including a short tutorial. A longer tutorial is also available, courtesy of the Internet Archive.","permalink":"/moodledevdocs/docs/4.4/apis/subsystems/form/usage/"},{"id":"apis/subsystems/form/index","title":"Forms API","description":"Form are created using the Form API. The Form API supports most standard HTML elements, including checkboxes, radio buttons, text boxes, and so on, adding additional accessibility and security features to them.","permalink":"/moodledevdocs/docs/4.4/apis/subsystems/form/"}],"unlisted":false}')}}]);