"use strict";(self.webpackChunkdevdocs=self.webpackChunkdevdocs||[]).push([[76219],{17208:e=>{e.exports=JSON.parse('{"label":"Custom field","permalink":"/moodledevdocs/docs/4.4/tags/custom-field","allTagsPath":"/moodledevdocs/docs/4.4/tags","count":2,"items":[{"id":"apis/plugintypes/customfield/index","title":"Custom fields","description":"Custom fields allow you to create field types to be used for custom fields. Instances of these field types can be added to the respective areas that implement Custom fields API. Currently in Moodle core only courses implement this API, however custom fields are also used in addon plugins for other areas. For example, if you want to display radio buttons on the course edit page, then you can add an instance of a radio custom field plugin to the Course custom fields configuration.","permalink":"/moodledevdocs/docs/4.4/apis/plugintypes/customfield/"},{"id":"apis/core/customfields/index","title":"Custom fields API","description":"Custom fields API overview","permalink":"/moodledevdocs/docs/4.4/apis/core/customfields/"}],"unlisted":false}')}}]);