"use strict";(self.webpackChunkdevdocs=self.webpackChunkdevdocs||[]).push([[98670],{59105:e=>{e.exports=JSON.parse('{"label":"core_dml","permalink":"/moodledevdocs/docs/4.3/tags/core-dml","allTagsPath":"/moodledevdocs/docs/4.3/tags","count":5,"items":[{"id":"apis/core/dml/ddl","title":"Data definition API","description":"In this page you\'ll access to the available functions under Moodle to be able to handle DB structures (tables, fields, indexes...).","permalink":"/moodledevdocs/docs/4.3/apis/core/dml/ddl"},{"id":"apis/core/dml/index","title":"Data manipulation API","description":"This page describes the functions available to access data in the Moodle database. You should exclusively use these functions in order to retrieve or modify database content because these functions provide a high level of abstraction and guarantee that your database manipulation will work against different RDBMSes.","permalink":"/moodledevdocs/docs/4.3/apis/core/dml/"},{"id":"apis/core/dml/drivers","title":"DML drivers","description":"A number of native drivers are included with Moodle, including those with support for:","permalink":"/moodledevdocs/docs/4.3/apis/core/dml/drivers"},{"id":"apis/core/dml/exceptions","title":"DML exceptions","description":"The DML API uses a selection of exceptions to indicate errors.","permalink":"/moodledevdocs/docs/4.3/apis/core/dml/exceptions"},{"id":"apis/core/dml/delegated-transactions","title":"Transactions","description":"Moodle allows data manipulation to take place within a database transaction, known as a Delegated transaction. This allows you to perform CRUD operations, and roll them back if a failure takes place.","permalink":"/moodledevdocs/docs/4.3/apis/core/dml/delegated-transactions"}],"unlisted":false}')}}]);