"use strict";(self.webpackChunkdevdocs=self.webpackChunkdevdocs||[]).push([[46219],{68106:e=>{e.exports=JSON.parse('{"label":"Course","permalink":"/moodledevdocs/docs/4.3/tags/course","allTagsPath":"/moodledevdocs/docs/4.3/tags","count":2,"items":[{"id":"apis/plugintypes/customfield/index","title":"Custom fields","description":"Custom fields allow you to create field types to be used for custom fields. Instances of these field types can be added to the respective areas that implement Custom fields API. Currently in Moodle core only courses implement this API, however custom fields are also used in addon plugins for other areas. For example, if you want to display radio buttons on the course edit page, then you can add an instance of a radio custom field plugin to the Course custom fields configuration.","permalink":"/moodledevdocs/docs/4.3/apis/plugintypes/customfield/"},{"id":"apis/subsystems/group/index","title":"Groups API","description":"Moodle Groups are a way of expressing collections of users within a course. They may be defined by the teacher in the course participants page, or created automatically during a bulk user upload (for example, from a text file).","permalink":"/moodledevdocs/docs/4.3/apis/subsystems/group/"}],"unlisted":false}')}}]);